              console.log("%s moved from %d to %d", xIsNext ? "X" : "O", selectedSquare, i)
              nextSquares[selectedSquare] = null
              nextSquares[i] = xIsNext ? "X" : "O";
              sethasSelected(false);
              onPlay(nextSquares);
            }
            else{
              console.log("Already full: select another move")
              sethasSelected(false);
              setselectedSquare(null);
            }
          }
          else{
            console.log("Invalid move: select another move")
            sethasSelected(false);
            setselectedSquare(null);
          }
        }
        if(selectedSquare === 8){
          if(i === 4 || i === 5 || i === 7){
            if(squares[i]===null){
              console.log("%s moved from %d to %d", xIsNext ? "X" : "O", selectedSquare, i)
              nextSquares[selectedSquare] = null
              nextSquares[i] = xIsNext ? "X" : "O";
              sethasSelected(false);
              onPlay(nextSquares);
            }
            else{
              console.log("Already full: select another move")
              sethasSelected(false);
              setselectedSquare(null);
            }
          }
          else{
            console.log("Invalid move: select another move")
            sethasSelected(false);
            setselectedSquare(null);
          }
        }
      }
    }
  }

  const moves = history.map((squares, move) => {
    let description;
    if (move > 0){
      description = 'Go to move #' + move;
    }
    else{
      description = 'Go to game start';
    }
    return (
      <li key={move}>
        <button onClick={() => jumpTo(move)}>{description}</button>
      </li>
    );
  });

  return (
    <div className="game">
      <div className="game-board">
        <Board xIsNext={xIsNext} squares={currentSquares} onPlay={handlePlay} currentMove={currentMove} selectMove={selectMove}/>
      </div>
      <div className="game-info">
        <ol>{moves}</ol>
      </div>
    </div>
  )
}
